mapper.select:
select s.id,s.name,s.college,s.subject,s.grade,s.sclass,s.gender,s.phone,s.dorm_id,d.building,d.room,d.bunk
    from student as s left join bunk as d on  s.dorm_id = d.id


        s.id = #{id}


        and s.name like concat('%',#{name},'%')


        and s.college = #{college}


        and s.subject = #{subject}


        and s.grade = #{grade}


        and s.sclass = #{sclass}


        and d.building = #{building}


        and d.room = #{room}


        and d.bunk = #{bunk}


order by s.id desc
limit #{pageNum},#{pageSize}

select count(*) from student as s left join dorm as d on  s.dorm_id = d.id


        and  s.id = #{id}


        and s.name like concat('%',#{name},'%')


        and  s.college = #{college}


        and s.subject = #{subject}


        and  s.grade = #{grade}


        and s.sclass = #{sclass}


        and d.building = #{building}


        and d.room = #{room}


        and d.bunk = #{bunk}

select s.id,s.name,s.college,s.subject,s.grade,s.sclass,s.gender,s.phone,s.dorm_id,d.building,d.room,d.bunk
from student as s left join bunk as d on  s.dorm_id = d.id


        s.id = #{id}


        and s.name like concat('%',#{name},'%')


        and s.college = #{college}


        and s.subject = #{subject}


        and s.grade = #{grade}


        and s.sclass = #{sclass}


        and d.building = #{building}


        and d.room = #{room}


        and d.bunk = #{bunk}


order by s.id desc
